mapper.select:
SELECT wc.avatar, wc.`name` as customerName, wc.customer_type as customerType, wc.tag_ids as tagIds, wc.external_userid, wc.first_user_id, wc.track_state, wc.add_method, wc.first_add_time, wc.birthday, wc.gender, wc.phone, wc.email, wc.address, wc.qq, wc.position, wc.corp_name as corpName, wc.other_descr as otherDescr, (SELECT GROUP_CONCAT(wu.user_name) FROM we_user wu WHERE wu.user_id=wc.first_user_id) as userName, (SELECT GROUP_CONCAT(wt.tag_id) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.first_user_id=wfctr.user_id AND wt.tag_type=3 ) as personTagIds, (SELECT GROUP_CONCAT(wt.name) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.first_user_id=wfctr.user_id AND wt.tag_type=3 ) as personTagNames, (SELECT GROUP_CONCAT(wt.name) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.first_user_id=wfctr.user_id AND wt.tag_type=1 ) as tagNames FROM we_customer wc JOIN ( SELECT wcr.external_userid,wcr.first_user_id FROM we_customer wcr
AND wcr.gender=#{weCustomerList.gender}
AND wcr.track_state=#{weCustomerList.trackState}
AND wcr.add_method=#{weCustomerList.addMethod}
AND wcr.customer_type=#{weCustomerList.customerType}
AND wcr.external_userid=#{weCustomerList.externalUserid}
AND wcr.first_user_id=#{weCustomerList.firstUserId}
AND wcr.del_flag = #{weCustomerList.delFlag}
AND wcr.`name` like concat('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')
AND wcr.first_user_id in
#{item}
AND FIND_IN_SET(#{weCustomerList.tagIds},wcr.tag_ids)
AND date_format(wcr.first_add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}
ORDER BY wcr.first_add_time desc
limit ${pageDomain.pageSize * (pageDomain.pageNum-1)}, #{pageDomain.pageSize}
) a ON a.external_userid=wc.external_userid AND a.first_user_id=wc.first_user_id

SELECT count(0) FROM we_customer wcr


        AND wcr.gender=#{weCustomerList.gender}



        AND wcr.track_state=#{weCustomerList.trackState}



        AND wcr.add_method=#{weCustomerList.addMethod}



        AND wcr.customer_type=#{weCustomerList.customerType}



        AND wcr.external_userid=#{weCustomerList.externalUserid}



        AND wcr.first_user_id=#{weCustomerList.firstUserId}



        AND wcr.del_flag = #{weCustomerList.delFlag}


        AND  wcr.`name` like concat('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')


        AND wcr.first_user_id in

            #{item}



        AND   FIND_IN_SET(#{weCustomerList.tagIds},wcr.tag_ids)


        AND  date_format(wcr.first_add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}


ORDER BY wcr.first_add_time desc

mapper.insert:
INSERT INTO we_customer( external_userid, NAME, avatar, customer_type, gender, unionid, birthday, corp_name, position, is_open_chat, create_time, create_by, first_user_id, first_add_time, qq, email, address, phone, add_method, del_flag, state, tag_ids ) values
(#{item.externalUserid},#{item.customerName},#{item.avatar},#{item.customerType},#{item.gender}, #{item.unionid},#{item.birthday}, #{item.corpName},#{item.position},#{item.isOpenChat}, #{item.createTime}, #{item.createBy},#{item.firstUserId},#{item.firstAddTime},#{item.qq},#{item.email},#{item.address},#{item.phone},#{item.addMethod},#{item.delFlag}, #{item.state},#{item.tagIds} )
ON DUPLICATE KEY UPDATE NAME=IFNULL(VALUES(NAME),we_customer.NAME), avatar=IFNULL(VALUES(avatar), we_customer.avatar), customer_type=IFNULL(VALUES(customer_type),we_customer.customer_type), gender=IFNULL(VALUES(gender), we_customer.gender), unionid=IFNULL(VALUES(unionid), we_customer.unionid), birthday=IFNULL(VALUES(birthday), we_customer.birthday), corp_name=IFNULL(VALUES(corp_name), we_customer.corp_name), position=IFNULL(VALUES(position), we_customer.position), is_open_chat=IFNULL(VALUES(is_open_chat), we_customer.is_open_chat), first_user_id=IFNULL(VALUES(first_user_id), we_customer.first_user_id), first_add_time=IFNULL(VALUES(first_add_time),we_customer.first_add_time), qq=IFNULL(VALUES(qq), we_customer.qq), email=IFNULL(VALUES(email), we_customer.email), address=IFNULL(VALUES(address), we_customer.address), phone=IFNULL(VALUES(phone), we_customer.phone), add_method=IFNULL(VALUES(add_method), we_customer.add_method), del_flag=IFNULL(VALUES(del_flag),we_customer.del_flag), del_flag=IFNULL(VALUES(del_flag),we_customer.del_flag), state=IFNULL(VALUES(state),we_customer.state), tag_ids=IFNULL(VALUES(tag_ids),we_customer.tag_ids);

mapper.select:
SELECT
   COUNT(DISTINCT  wc.external_userid)
FROM
we_customer wc


        AND wc.gender=#{weCustomerList.gender}


        AND wc.track_state=#{weCustomerList.trackState}


        AND wc.add_method=#{weCustomerList.addMethod}



        AND wc.customer_type=#{weCustomerList.customerType}



        AND wc.external_userid=#{weCustomerList.externalUserid}



        AND wc.first_user_id=#{weCustomerList.firstUserId}




        AND wc.del_flag = #{weCustomerList.delFlag}


        AND  wc.`name` like concat('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')


        AND wc.first_user_id in

            #{item}



        AND   FIND_IN_SET(#{weCustomerList.tagIds},wc.tag_ids)


        AND  date_format(wc.first_add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}
