mapper.select:
SELECT wc.avatar, wc.`name` as customerName, wc.customer_type as customerType, GROUP_CONCAT(if(wt.tag_type=1,wt.`tag_id`,null )) as tagIds, GROUP_CONCAT(if(wt.tag_type=1,wt.`name`,null )) as tagNames, GROUP_CONCAT(if(wt.tag_type=3,wt.`tag_id`,null )) as personTagIds, GROUP_CONCAT(if(wt.tag_type=3,wt.`name`,null )) as personTagNames, wu.user_name, wc.external_userid, wc.first_user_id, wc.track_state, wc.add_method, wc.first_add_time, wc.birthday, wc.gender, (SELECT GROUP_CONCAT(wd.`name`) FROM we_department wd WHERE wd.id in (wu.department)) as departmentName, wc.phone, wc.email, wc.address, wc.qq, wc.position, wc.corp_name as corpName, wc.other_descr as otherDescr FROM we_customer wc LEFT JOIN we_user wu ON wc.first_user_id=wu.user_id LEFT JOIN we_flower_customer_tag_rel wfctr ON wfctr.del_flag=0 AND wfctr.external_userid=wc.external_userid AND wfctr.user_id=wc.first_user_id LEFT JOIN we_tag wt ON wt.tag_id=wfctr.tag_id
AND wc.gender=#{weCustomerList.gender}
AND wc.track_state=#{weCustomerList.trackState}
AND wc.add_method=#{weCustomerList.addMethod}
AND wc.customer_type=#{weCustomerList.customerType}
AND wc.external_userid=#{weCustomerList.externalUserid}
AND wc.first_user_id=#{weCustomerList.firstUserId}
OR wu.department in
#{item}
AND wc.del_flag = #{weCustomerList.delFlag}
AND wc.`name` like concat('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')
AND wc.first_user_id in
#{item}
AND wt.tag_id in
#{item}
AND date_format(wc.first_add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}
GROUP BY wc.external_userid,wc.first_user_id ORDER BY wc.first_add_time desc

mapper.insert:
INSERT INTO we_customer( external_userid, NAME, avatar, customer_type, gender, unionid, birthday, corp_name, position, is_open_chat, create_time, create_by, first_user_id, first_add_time, qq, email, address, phone, add_method, del_flag, state ) values
(#{item.externalUserid},#{item.customerName},#{item.avatar},#{item.customerType},#{item.gender}, #{item.unionid},#{item.birthday}, #{item.corpName},#{item.position},#{item.isOpenChat}, #{item.createTime}, #{item.createBy},#{item.firstUserId},#{item.firstAddTime},#{item.qq},#{item.email},#{item.address},#{item.phone},#{item.addMethod},#{item.delFlag}, #{item.state} )
ON DUPLICATE KEY UPDATE NAME=IF(TRIM(VALUES(NAME)) !='',VALUES(NAME),we_customer.NAME), avatar=IF(TRIM(VALUES(avatar)) !='', VALUES(avatar), we_customer.avatar), customer_type=IF(VALUES(customer_type),VALUES(customer_type),we_customer.customer_type), gender=IF(VALUES(gender), VALUES(gender), we_customer.gender), unionid=IF(TRIM(VALUES(unionid)) !='', VALUES(unionid), we_customer.unionid), birthday=IF(VALUES(birthday),VALUES(birthday), we_customer.birthday), corp_name=IF(TRIM(VALUES(corp_name)) !='', VALUES(corp_name), we_customer.corp_name), position=IF(TRIM(VALUES(position)) != '',VALUES(position) , we_customer.position), is_open_chat=IF(VALUES(is_open_chat), VALUES(is_open_chat), we_customer.is_open_chat), first_user_id=IF(TRIM(VALUES(first_user_id)) !='', VALUES(first_user_id), we_customer.first_user_id), first_add_time=IF(VALUES(first_add_time), VALUES(first_add_time), we_customer.first_add_time), qq=IF(TRIM(VALUES(qq)) !='', VALUES(qq), we_customer.qq), email=IF(TRIM(VALUES(email)) != '', VALUES(email), we_customer.email), address=IF(TRIM(VALUES(address)) != '', VALUES(address), we_customer.address), phone=IF(TRIM(VALUES(phone)) != '', VALUES(phone), we_customer.phone), add_method=IF(VALUES(add_method), VALUES(add_method), we_customer.add_method), del_flag=IF(VALUES(del_flag),VALUES(del_flag),we_customer.del_flag), state=IF(TRIM(VALUES(state)) != '',VALUES(state),we_customer.state);

