mapper.select:

LEFT JOIN we_pres_tag_group_scope wptgs ON wptgs.task_id = wptg.task_id
WHERE wptgs.we_user_id = #{emplId}
AND wptgs.is_done = #{isDone}
AND wptg.send_type = 1 AND wptg.`del_flag` = 0
ORDER BY  wptg.create_time DESC

SELECT DISTINCT u.`user_id` FROM we_user u LEFT JOIN we_flower_customer_rel wfcr ON u.`user_id` = wfcr.`user_id` LEFT JOIN we_customer wc ON wc.external_userid = wfcr.external_userid LEFT JOIN we_flower_customer_tag_rel wfctr ON wfcr.`id` = wfctr.`flower_customer_rel_id` LEFT JOIN we_pres_tag_group_tag gtr ON wfctr.`tag_id` = gtr.`tag_id` LEFT JOIN we_pres_tag_group task ON task.`task_id` = gtr.`task_id` WHERE task.`task_id` = #{taskId}
AND wc.`gender` = #{sendGender}
and date_format(wfcr.`create_time`,'%y%m%d') >= date_format(#{cusBeginTime},'%y%m%d')
and date_format(wfcr.`create_time`,'%y%m%d') <= date_format(#{cusEndTime},'%y%m%d')

mapper.update:
UPDATE we_pres_tag_group
task_name = #{taskName}, send_type = #{sendType}, group_code_id = #{groupCodeId}, welcome_msg = #{welcomeMsg}, send_scope = #{sendScope}, send_gender = #{sendGender}, cus_begin_time = #{cusBeginTime}, cus_end_time = #{cusEndTime}, update_by = #{updateBy}, update_time = #{updateTime},
WHERE task_id = #{taskId}
