select.where:
and user_id = #{userId}
and user_name like concat('%', #{name}, '%')
and alias = #{alias}
and gender = #{gender}
and mobile = #{mobile}
and email = #{email}
and wx_account = #{wxAccount}
and FIND_IN_SET(#{department},department)
and position = #{position}
and is_leader_in_dept = #{isLeaderInDept}

    AND date_format(join_time,'%Y-%m-%d') >= date_format(#{beginTime},'%Y-%m-%d')


    AND date_format(join_time,'%Y-%m-%d') <= date_format(#{endTime},'%Y-%m-%d')

and id_card = #{idCard}
and qq_account = #{qqAccount}
and telephone = #{telephone}
and address = #{address}
and birthday = #{birthday}
and is_activate = #{isActivate}
and is_open_chat = #{isOpenChat}
and is_activate !=6

mapper.select:
SELECT * FROM ( SELECT wu.user_id, wu.user_name, wu.dimission_time, (SELECT GROUP_CONCAT(wd.`name`) FROM we_department wd WHERE wd.id=wu.department) as department, (SELECT COUNT(*) FROM we_flower_customer_rel wfcr WHERE wfcr.user_id=wu.user_id) as allocateCustomerNum, (SELECT COUNT(*) FROM we_group wg WHERE wg.`owner`=wu.user_id) as allocateGroupNum FROM we_user wu
and wu.user_name like concat('%', #{userName}, '%')
AND date_format(wu.dimission_time,'%Y-%m-%d') >= date_format(#{beginTime},'%Y-%m-%d')
AND date_format(wu.dimission_time,'%Y-%m-%d') <= date_format(#{endTime},'%Y-%m-%d')
and wu.is_activate = #{isActivate} and wu.is_allocate = #{isAllocate}
)wuu WHERE allocateCustomerNum >0 and allocateGroupNum>0

SELECT wc.`name` as customerName, wu.user_name as takeUserName, wac.allocate_time, (SELECT GROUP_CONCAT(wd.`name`) FROM we_department wd WHERE wd.id=wu.department) as department FROM we_allocate_customer wac LEFT JOIN we_customer wc ON wac.external_userid=wc.external_userid LEFT JOIN we_user wu ON wu.user_id=wac.takeover_userid
and wac.handover_userid=#{handoverUserId}
DATE_FORMAT(wu.dimission_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}

SELECT wu.user_name as newOwnerName, wg.chat_id, wg.group_name, (SELECT count(*) FROM we_group_member wgm WHERE wgm.chat_id=wg.chat_id) as memberNum, (SELECT GROUP_CONCAT(wd.`name`) FROM we_department wd WHERE wd.id=wu.department) as department, wag.allocate_time FROM we_allocate_group wag LEFT JOIN we_group wg ON wag.chat_id=wg.chat_id LEFT JOIN we_user wu ON wu.user_id=wag.new_owner
and wag.old_owner=#{oldOwner}
DATE_FORMAT(wu.dimission_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
